import { useEffect, useState } from 'react';
import ExpenseItem from './ExpenseItem';
import './Expense.css';


// function filterExpense(collection, source) {
//   var pairs = Object.entries(source);
//   return collection.filter(object => pairs.every(([key, value]) => object[key] === value));
// }

const AvailableExpenses = (month, year) => {
  const [expenses, setExpenses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        'https://expense-e9126-default-rtdb.firebaseio.com/expense.json'
      );


      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedExpenses = [];

      for (const key in responseData) {
        loadedExpenses.push({
          id: key,
          Description: responseData[key].Description,
          Year: responseData[key].Year,
          Amount: responseData[key].Amount,
          Day: responseData[key].Day,
          Month: responseData[key].Month,

        });
      }

      // setExpenses(loadedExpenses);
      setIsLoading(false);
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section>
        <p>{httpError}</p>
      </section>
    );
  }


  const source = {
    Month: month.month,
    Year: month.year,
  }

  var pairs = Object.entries(source);

  setExpenses(loadedExpenses.filter(object => pairs.every(([key, value]) => object[key] === value)));
  console.log(expenses)


  const ExpenseList = expenses.map((expense) => (
    <ExpenseItem
      key={expense.id}
      id={expense.id}
      Description={expense.Description}
      Year={expense.Year}
      Amount={expense.Amount}
      Day={expense.Day}
      Month={expense.Month}
    />
  ));
console.log(ExpenseList)

  return (

    <ul>{ExpenseList}</ul>

  );
};

export default AvailableExpenses;
