import { useEffect, useState } from 'react';
import ExpenseItem from './ExpenseItem';
import './Expense.css';


// function filterExpense(collection, source) {
//   var pairs = Object.entries(source);
//   return collection.filter(object => pairs.every(([key, value]) => object[key] === value));
// }

const AvailableExpenses = (month, year) => {
  const [expenses, setExpenses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        'https://expense-e9126-default-rtdb.firebaseio.com/expense.json'
      );


      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedExpenses = [];

      for (const key in responseData) {
        loadedExpenses.push({
          id: key,
          Description: responseData[key].Description,
          Year: responseData[key].Year,
          Amount: responseData[key].Amount,
          Day: responseData[key].Day,
          Month: responseData[key].Month,

        });
      }

      setExpenses(loadedExpenses);
      setIsLoading(false);
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section>
        <p>{httpError}</p>
      </section>
    );
  }


  const source = {
    Month: month.month,
    Year: month.year,
  }



  var ExpenseList = {}
  var pairs = Object.entries(source);
  console.log(pairs)


  if(pairs[0][1]!==pairs[1][1]){

    const ExpenseListFilters =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
    console.log(ExpenseListFilters)
    ExpenseList = ExpenseListFilters.map((expense) => (
      <ExpenseItem
        key={expense.id}
        id={expense.id}
        Description={expense.Description}
        Year={expense.Year}
        Amount={expense.Amount}
        Day={expense.Day}
        Month={expense.Month}
      />
    ));
    console.log(ExpenseListFilters)

 }

  if(pairs[0][1]===pairs[1][1]){
     ExpenseList = expenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        id={expense.id}
        Description={expense.Description}
        Year={expense.Year}
        Amount={expense.Amount}
        Day={expense.Day}
        Month={expense.Month}
      />
    ));
  }

    if(pairs[1][1]!=="" && pairs.length===2){
    pairs.shift()
    const ExpenseListFilters =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
    ExpenseList = ExpenseListFilters.map((expense) => (
      <ExpenseItem
        key={expense.id}
        id={expense.id}
        Description={expense.Description}
        Year={expense.Year}
        Amount={expense.Amount}
        Day={expense.Day}
        Month={expense.Month}
      />
    ));
    console.log(ExpenseList)
  }

  if(pairs[0][1]!=="" && pairs.length===2){
    pairs.pop()
    const ExpenseListFilters =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
    ExpenseList = ExpenseListFilters.map((expense) => (
      <ExpenseItem
        key={expense.id}
        id={expense.id}
        Description={expense.Description}
        Year={expense.Year}
        Amount={expense.Amount}
        Day={expense.Day}
        Month={expense.Month}
      />
    ));
    console.log(ExpenseList)

  }







  // const ExpenseList = expenses.map((expense) => (
  //   <ExpenseItem
  //     key={expense.id}
  //     id={expense.id}
  //     Description={expense.Description}
  //     Year={expense.Year}
  //     Amount={expense.Amount}
  //     Day={expense.Day}
  //     Month={expense.Month}
  //   />
  // ));
console.log(ExpenseList)

  return (

    <ul>{ExpenseList}</ul>

  );
};

export default AvailableExpenses;
