import { useEffect, useState, useReducer } from 'react';
import ExpenseItem from './ExpenseItem';
import './Expense.css';




const AvailableMembers = ({month,year}) => {
  const [expenses, setExpenses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();
  const [ignored, forceUpdate] = useReducer(x => x + 1, 0);

  useEffect(() => {
    const fetchExpenses = async () => {
      const response = await fetch(
        'https://expense-e9126-default-rtdb.firebaseio.com/socialclub.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();
      forceUpdate();

      const loadedExpenses = [];

      for (const key in responseData) {
        loadedExpenses.push({
          id: key,
          Name: responseData[key].Name,
          Age: responseData[key].Age,
          Twitter: responseData[key].Twitter,
          Instagram: responseData[key].Instagram,
          Email: responseData[key].Email,

        });
      }

      setExpenses(loadedExpenses);
      setIsLoading(false);
    };

    fetchExpenses().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, [ignored]);

  if (isLoading) {
    return (
      <section>
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section>
        <p>{httpError}</p>
      </section>
    );
  }


  const source = {
    Month: {month}.month,
    Year: {year}.year,
  }



  var ExpenseList = {}
  var pairs = Object.entries(source);
  console.log(pairs)



  if(pairs[0][1]===pairs[1][1]){
     ExpenseList = expenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        id={expense.id}
        Name={expense.Name}
        Age={expense.Age}
        Twitter={expense.Twitter}
        Email={expense.Email}
        Instagram={expense.Instagram}
      />
    ));
  }

  if(pairs[0][1]!=="" && pairs[1][1]!==""){

    const ExpenseListFilters =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
    // expenses =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
    ExpenseList = ExpenseListFilters.map((expense) => (
      <ExpenseItem
      key={expense.id}
      id={expense.id}
      Name={expense.Name}
      Age={expense.Age}
      Twitter={expense.Twitter}
      Email={expense.Email}
      Instagram={expense.Instagram}
      />
    ));
    console.log(ExpenseList)

 }

 if(pairs[0][1]!=="" && pairs[1][1]===""){

  const ExpenseListFilters = expenses.filter(element => { return element.Month === pairs[0][1];})
  // expenses =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
  ExpenseList = ExpenseListFilters.map((expense) => (
    <ExpenseItem
    key={expense.id}
    id={expense.id}
    Name={expense.Name}
    Age={expense.Age}
    Twitter={expense.Twitter}
    Email={expense.Email}
    Instagram={expense.Instagram}
    />
  ));
  console.log(ExpenseList)

}

if(pairs[0][1]==="" && pairs[1][1]!==""){

  const ExpenseListFilters = expenses.filter(element => { return element.Year === pairs[1][1];})
  // expenses =  expenses.filter(object => pairs.every(([key, value]) => object[key] === value))
  ExpenseList = ExpenseListFilters.map((expense) => (
    <ExpenseItem
    key={expense.id}
    id={expense.id}
    Name={expense.Name}
    Age={expense.Age}
    Twitter={expense.Twitter}
    Email={expense.Email}
    Instagram={expense.Instagram}
    />
  ));
  console.log(ExpenseList)

}





  return (

    <ul>{ExpenseList}</ul>

  );


};

export default AvailableMembers;
